import {
  useState,
  useEffect
} from "react";

function App() {
  return ( <
    div >
    <
    List / >
    <
    /div>
  );
}

const List = () => {
    const [todos, setTodos] = useState([]);
    const [text, setText] = useState('');

    const fetchTodos = async () => {
      const res = await fetch('http://localhost:3001/todos');
      setTodos(await res.json());
    };

    const addTodo = async () => {
      const res = await fetch('http://localhost:3001/todos', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({
          desc: text,
          done: false
        })
      });

      fetchTodos();
      setText('');
    };

    useEffect(() => {
      fetchTodos();
    }, [])

    const items = todos.map(todo => < Item todo = {
        todo
      }
      key = {
        todo._id
      }
      />)

      return ( <
        div className = "ui relaxed divided list" > {
          items
        } <
        form onSubmit = {
          (e) => e.preventDefault()
        } >
        <
        input value = {
          text
        }
        onChange = {
          (e) => setText(e.target.value)
        }
        /> <
        input type = "submit"
        onClick = {
          addTodo
        }
        /> <
        /form> <
        /div>
      )
    }

    const Item = (props) => {
      const {
        done,
        desc
      } = props.todo;

      return ( < div > < div className = "item" > {
        desc
      } < /div></div > )
    }

    export default App;
